import React, { useEffect, useState } from 'react';
import { collection, getDocs,query,where} from 'firebase/firestore';
import { db} from '../../firebase_setup/firebase';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import cities from '../Contact_Form_Component/cities.json';


import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';
import { Bar, Pie } from 'react-chartjs-2';
// import { Direction } from 'react-toastify/dist/utils';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

function DataGraphs() {
  const [reports, setReports] = useState([]);
  const [year, setYear] = useState('');
  const [city, setCity] = useState('');
  let schoolNum = 0;
let youthNum = 0;
let otherNum = 0;
  const [config, setConfig] = useState({
    chart: {
      type: 'line',
    },
    title: {
      display: true,
      text:'כמויות דיווחים לפי שנה, חודש ועיר',
      style: {
        fontSize: '40px', // Set the font size for the title
      }, 
    },
    xAxis: {
      title: {
        text: 'חודשים',
      },
      categories: [
        'ינואר',
        'פברואר',
        'מרץ',
        'אפריל',
        'מאי',
        'יוני',
        'יולי',
        'אוגוסט',
        'ספטמבר',
        'אוקטובר',
        'נובמבר',
        'דצמבר',
      ],
    },
    yAxis: {
      title: {
        text: 'כמות דיווחים',
      },
    },
    series: [
      {
        name: 'Data',
        data: [1, 5, 3, 4, 8, 8, 8, 8, 8, 8, 8, 8],
      },
    ],
    plotOptions: {
      series: {
        line: {
          color: '#ff0000', // Change the line color
          lineWidth: 2, // Change the line width
        },
      },
    },
  });

  /* reading from firebase */
  useEffect(() => {
    const fetchReports = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, 'Reports'));
        const newData = querySnapshot.docs.map((doc) => ({
          ...doc.data(),
          id: doc.id,
        }));
        setReports(newData);
      } catch (error) {
        console.error('Error fetching reports: ', error);
      }
    };

    fetchReports();
  }, []);


  /*---------------------------------------------------------------------------------------*/
  /* by city */
  let places = [];
  places = reports
    .map((item) => item['city'])
    .filter((value) => value !== null && value !== '' && !(places.includes(value)));
  const placesCount = places.reduce((countMap, city) => {
    countMap[city] = (countMap[city] || 0) + 1;
    return countMap;
  }, {});

  const CityData = {
    labels: Object.keys(placesCount),
    datasets: [
      {
        data: Object.values(placesCount),
        backgroundColor: 'rgba(108,47,212,0.58)',
        borderColor: 'black',
        borderWidth: 1,
      },
    ],
  };
  

  /*---------------------------------------------------------------------------------------*/
  /* by urgency */
  const urgencyArr = reports
    .map((item) => item['urgency'])
    .filter((value) => value !== null && value !== '');
  const countUrgency = urgencyArr.filter((urgency) => urgency === 'דחוף').length;
  const countNotUrgency = urgencyArr.length - countUrgency;

  const UrgencyData = {
    labels: ['דחוף', 'לא דחוף'],
    datasets: [
      {
        data: [countUrgency, countNotUrgency],
        backgroundColor: ['rgba(233, 31, 19, 1)', 'rgba(15, 255, 11, 1)'],
        borderWidth: 1,
      },
    ],
  };

  /*---------------------------------------------------------------------------------------*/
  /* by year and city => months */
  /* when click on the search */
  function handleSearch(event) {
    if(city === ''){
      const filteredReports = reports.filter((item) => {
        const reportYear = new Date(item.date).getFullYear();
        return reportYear.toString() === year.toString();
      });
  
      const monthlyCounts = Array(12).fill(0);
      filteredReports.forEach((item) => {
        const monthIndex = new Date(item.date).getMonth();
        monthlyCounts[monthIndex]++;
      });
  
      const updatedConfig = {
        ...config,
        series: [
          {
            name: 'Data',
            data: monthlyCounts,
          },
        ],
      };
      
      setConfig(updatedConfig);
    }
  
    else{

    const filteredReports = reports.filter((item) => {
      const reportYear = new Date(item.date).getFullYear();
      return reportYear.toString() === year.toString() && item["city"] === city;
    });

    const monthlyCounts = Array(12).fill(0);
    filteredReports.forEach((item) => {
      const monthIndex = new Date(item.date).getMonth();
      monthlyCounts[monthIndex]++;
    });

    const updatedConfig = {
      ...config,
      series: [
        {
          name: 'Data',
          data: monthlyCounts,
        },
      ],
    };
    setConfig(updatedConfig);
  }
  }

  /*----------------------------------------by places =>[school , Youth, other ]-----------------------------------*/

async function fetchReports() {
  const reportCollectionRef = collection(db, "Reports");
  const querySnapshot1 = await getDocs(query(reportCollectionRef, where("place", "==", "בית ספר")));
  const querySnapshot2 = await getDocs(query(reportCollectionRef, where("place", "==", "תנועת נוער")));
  const querySnapshot3 = await getDocs(query(reportCollectionRef, where("place", "==", "אחר")));
  const allQuerySnapshot = await getDocs(query(reportCollectionRef ));
  if(allQuerySnapshot !== 0){
  schoolNum = ((querySnapshot1.size / allQuerySnapshot.size) * 100);
  youthNum = ((querySnapshot2.size / allQuerySnapshot.size) * 100);
  otherNum = ((querySnapshot3.size / allQuerySnapshot.size) * 100);}
}


const config1 = ( {
  chart: {
      plotBackgroundColor: null,
      plotBorderWidth: null,
      plotShadow: false,
      type: 'pie'
  },
  title: {
      text: 'חלוקת מקומות שבהם נעשים חרמות',
      align: 'center',
  },
  tooltip: {
      pointFormat: '{series.name}: <b>{point.percentage:.1f}%</b>'
  },
  accessibility: {
      point: {
          valueSuffix: '%'
      }
  },
  plotOptions: {
      pie: {
          allowPointSelect: true,
          cursor: 'pointer',
          dataLabels: {
              enabled: true,
              format: '<b>{point.name}</b>: {point.percentage:.1f} %'
          }
      }
  },
  series: [{
      name: 'Brands',
      colorByPoint: true,
      data: [{
          name: 'בתי ספר',
          y: fetchReports("בית ספר"),
          sliced: true,
          selected: true
      }, {
          name: 'תנועות נוער',
          y: fetchReports("תנועת נוער"),
      },  {
          name: 'אחר',
          y: fetchReports("אחר"),
      }]
  }]
});




  return (
    <div>
      <div>
        <Bar
          style={{
            margin: 'auto',
            padding: '5px',
            width: '80%',
            height: 'auto',
          }}
          data={CityData}
          options={{
            responsive: true,
            plugins: {
              legend: {
                display: false,
                position: 'top' ,
              },
              title: {
                display: true,
                text: 'כמות דיווחים לפי ערים',
                font: {
                  size: 40, // Set the font size for the title
                }, 
              },
            },
          }}
        ></Bar>
      </div>

      <div>
        <br></br>
        <Pie
          style={{
            margin: 'auto',
            padding: '5px',
            width: '400px',
            height: 'auto',
          }}
          data={UrgencyData}
          options={{
            responsive: true,
            plugins: {
              legend: {
                display: true,
                position: 'top' ,
              },
              title: {
                display: true,
