import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import HomeRgular from "./pages/HomePageRegularUser/HomeRegular";
import FollowSingleReportPage from "./pages/FollowSingleReportPage/FollowSingleReportPage";
import Login from "./pages/LoginPage/Login";
import ReportsTable from "./pages/ReportsTablePage/ReportsTable";
import DataGraphs from "./components/DataGraphs/DataGraphs";
import Signup from "./components/Signup_Component/Signup";
import Layout from "./components/Header_Component/Layout";
import { AuthContextProvider } from "./context/AuthContext";
import ProtectedRoute from "./components/ProtectedRoute";
import RemoveAdministrator from "./components/RemoveUser_Component/RemoveAdministraitor";
function App() {
  return (
    <div className="App">
      <Layout>
        <AuthContextProvider>
          <Routes>
            {/* public routs */}
            <Route path="/" element={<HomeRgular />} />
            <Route
              path="/FollowSingleReportPage"
              element={<FollowSingleReportPage />}
            />
            <Route path="/Login" element={<Login />} />

            {/* we want to protect this routs */}
            <Route
              path="/ReportsTable"
              element={
                <ProtectedRoute>
                  {" "}
                  <ReportsTable />{" "}
                </ProtectedRoute>
              }
            />
            <Route
              path="/DataGraphs"
              element={
                <ProtectedRoute>
                  {" "}
                  <DataGraphs />{" "}
                </ProtectedRoute>
              }
            />
            <Route
              path="/Signup"
              element={
                <ProtectedRoute>
                  {" "}
                  <Signup />{" "}
                </ProtectedRoute>
              }
            />
            <Route
              path="/RemoveUser"
              element={
                <ProtectedRoute>
                  {" "}
                  <RemoveAdministrator />{" "}
                </ProtectedRoute>
              }
            />

            {/* catch all */}
            <Route path="*" element={<h1>PAGE NOT FOUND</h1>} />
          </Routes>
        </AuthContextProvider>
      </Layout>
    </div>
  );
}

export default App;
